void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.y;

    uv *= 2.;
    uv -= vec2(2.5, 0.5);
    uv = clamp(uv, 0., 1.);

    float frame = floor(mod(iTime * 10.0, 6.0));

    // Rainbow effect
    float rainbowWidth = 0.8;
    float rainbowHeight = 0.15;
    float rainbowOffsetY = 0.05;
    float rainbowOffsetX = 0.1;

    float colorWidth = rainbowWidth / 6.0;

    if (uv.x > rainbowOffsetX && uv.x < (rainbowOffsetX + rainbowWidth) && uv.y > rainbowOffsetY && uv.y < (rainbowOffsetY + rainbowHeight)) {
        float rainbowPosition = mod((uv.x - rainbowOffsetX + iTime * 0.2) / colorWidth, 6.0);
        vec3 rainbowColor;
        if (rainbowPosition < 1.0) {
            rainbowColor = vec3(1.0, 0.0, 0.0); // Red
        } else if (rainbowPosition < 2.0) {
            rainbowColor = vec3(1.0, 0.5, 0.0); // Orange
        } else if (rainbowPosition < 3.0) {
            rainbowColor = vec3(1.0, 1.0, 0.0); // Yellow
        } else if (rainbowPosition < 4.0) {
            rainbowColor = vec3(0.0, 1.0, 0.0); // Green
        } else if (rainbowPosition < 5.0) {
            rainbowColor = vec3(0.0, 0.0, 1.0); // Blue
        } else {
            rainbowColor = vec3(0.5, 0.0, 0.5); // Purple
        }
        fragColor = vec4(rainbowColor, 1.0);
        return;
    }

    float nyanWidthUV = 40.0 / 256.0;
    uv.x = (uv.x + frame) * nyanWidthUV;

    vec4 nyan = texture(iChannel0, uv);
    vec3 col = nyan.xyz;

    fragColor = vec4(col, 1.0);
}
