#define R iResolution
#define PI 3.141592653589793
#define TAU (PI*2.0)
#define CL(X) clamp(X, 0.0, 1.0)

mat2 mr(float a) { float s = sin(a); float c = cos(a); return mat2(c, s, -s, c); }

const float K = 0.393939;
const float E = K*0.5;
const float C = E;
const float S = 3.6;
const float W = S*0.5;

void mainImage(out vec4 o, in vec2 fc)
{
    vec2 uv = ((fc - 0.5 * R.xy) / R.y) * mr(mod(iTime, TAU));
    float s = min(max(0.0, K-abs(uv.x)), max(0.0, K-abs(uv.y)));
    float ks = (s - (E+C*(0.5+0.5*sin(iTime*S+(s*s*s+W*(1.0/length(uv))))))) / (1.0 - exp(s));
    o = vec4(pow(mix(vec3(0.0)+mix(ks, sin(ks*TAU),
        0.5*abs(ks-clamp(abs((1.0 - exp(s)) / (1.0-exp(s-0.5))), 0.0, 1.0))),
        vec3(0.15, 0.25, 0.5), smoothstep(0.0, K, s))+(CL(0.5-ks*ks)*CL(ks)), vec3(1.0 / 2.2)), 1.0);
}
