vec3 voronoi(vec2 p) // modified https://www.shadertoy.com/view/ldl3W8 Voronoi - distances by iq
{
    vec2 ip = floor(p);
    vec2 fp = fract(p);

    //----------------------------------
    // first pass: regular voronoi
    //----------------------------------
    vec2 mg, mr;

    float md = 8.0;
    for(int j=-1; j<=+1; j++)
    {
        for(int i=-1; i<=+1; i++)
        {
            vec2 g = vec2(float(i),float(j));
            vec2 o = hash22(ip + g);
            vec2 r = g + o - fp;
            float d = dot(r,r);

            if(d < md)
            {
                md = d;
                mr = r;
                mg = g;
            }
        }
    }

    //----------------------------------
    // second pass: distance to borders
    //----------------------------------
    md = 8.0;
    for(int j=-2; j<=+2; j++)
    {
        for(int i=-2; i<=+2; i++)
        {
            vec2 g = mg + vec2(float(i),float(j));
            vec2 o = hash22( ip + g );
            vec2 r = g + o - fp;

            if(dot(mr-r,mr-r)>0.00001)
            md = min(md, dot(0.5*(mr+r), normalize(r-mr)));
        }
    }

    return vec3(ip+mg,md);
}

vec3 map(vec2 p)
{
    float s = 31.0;
    return voronoi(p*s)/s;
}

vec3 hsv( in vec3 c ){
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	rgb = rgb*rgb*(3.0-2.0*rgb);
	return c.z * mix( vec3(1.0), rgb, c.y);}
//smooth hsv from iq https://www.shadertoy.com/view/MsS3Wc

void mainImage(out vec4 q, vec2 p)
{
    vec2 R = iResolution.xy;
    vec2 I =p;
    p = (p + p - R) / R.y; // concise scaling thanks to Fabrice

    vec3 m = map(p);
    float d = m.z;
    vec2 id = m.xy;
    vec3 color = texture(iChannel0,(id*vec2(R.y/R.x,1)*0.5+0.5)).xyz;
    color *= color; // high contrast
    //color *= hash32(id*123.456);
    color *= smoothstep(0.002, 0.004, d);
    vec2 mid=vec2(R.x*.5,R.y*.5);

    vec3 light=vec3(mid.x+50.*sin(iTime),mid.y+20.*cos(iTime),40.);
    vec2 spot=vec2(mid.x+30.*sin(iTime*.3),mid.y+30.*cos(iTime*.3));
    vec3 lidir=normalize(vec3(light.xy-spot,light.z));
    vec3 mydir=normalize(vec3(light.xy-I,light.z));
    vec3 tint=hsv(vec3(iTime*.1+I.x/R.x*I.y/R.y,.3*abs(cos(iTime*.25)),1.));

    color = sqrt(color); // approx gamma

    q = vec4(100.*tint*pow(dot(lidir,mydir),3.)*color/distance(light,vec3(I,0.)),1.0);
}
