vec3 palette( float t ){
     vec3 a = vec3(.75,.5,.25);
     vec3 b = vec3(.1,.6,.8);
     vec3 c = vec3(0.7,.5,0.5);
     vec3 d = vec3(0.263,0.416,0.557);
     return a+b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2.0 -1.0;

    vec3 finalcol=vec3(0.);

    uv.x *=iResolution.x/iResolution.y;

    vec2 uv0=uv;

    for (float i=0.0;i<length(uv0)*2.;i++){

        uv = fract(uv) *2.0 -1.0;

        float d = tan(length(uv)) /exp(length(uv0));

        d=sin(d*8.+iTime)/2.;

        d=abs(d);

        d=pow(0.05/d,1.2);

        vec3 col = vec3(d,d,d);

        col = tan(col + sin(iTime)*4.)/8.;

        col=(palette(length(uv0)+(iTime)*0.4));

        finalcol +=col*d;
    }
    fragColor = vec4(finalcol,1.);
}
