#define rot(a) mat2(cos(a+vec4(0,33,11,0)))

// https://iquilezles.org/articles/distfunctions2d/
float sdBox( in vec2 p, in vec2 b ) {
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 O, in vec2 F ) {    
    vec2 R = iResolution.xy;
    vec2 uv = (F+F-R)/R.x*1.2;

    vec3 c = vec3(0);
    vec3 cB = vec3(0.078,0.11,0.867);        

    float b = sdBox(uv*rot(iTime*.1), vec2(.9));    
    b += (.02)*cos(atan(uv.x,uv.y)*333.+iTime*1.5);    
    //                             ^ try another number :)

    c = mix(c,cB,.01/abs(sin(b*16.-iTime*1.9)/16.));
    c = mix(c,cB,smoothstep(.01,.0,length(uv)-0.5));

    // https://x.com/YoheiNishitsuji/status/1842112423886770525
    for(float i=0.; i<99.; i++) {
        float j = i+9.;
        vec2 q = 0.6*(uv*0.5*rot(iTime*.1)) 
                - vec2(sin(iTime*.5-j/96.6)*1.5, sin(iTime*2.5-j)*1.5)*.1;		
        c = mix(c,vec3(0.576,0.024,0.89), 0.0045/(length(q*5.)-0.009));
    }

    O = vec4(c,1.0);
}
