vec4 hash42(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec4 p4 = fract(vec4(p.xyxy) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

#define pi 3.1415926
#define tau (pi+pi)

float square(ivec2 t, vec2 q)
{
    vec4 r = (hash42(vec2(t)*13.456)*0.5+0.5);
    float a = tau*r.w;
    mat2 R = mat2(cos(a),sin(a),-sin(a),cos(a));
    q = R*(q-0.5)+0.5;
    return 1.0-smoothstep(-0.01,+0.01,max(abs(q.x-r.x),abs(q.y-r.y))-r.z);
}

float fuzzy_xor(float a, float b)
{
    return max(min(a,b),1.0-max(a,b));
}

float tile_env(ivec2 t, vec2 q)
{
    float d = 0.0;
    for(int y = -1; y <= +1; y++)
        for(int x = -1; x <= +1; x++)
            d = fuzzy_xor(d,square(t+ivec2(x,y), q-vec2(x,y)));

    return d;
}

float random_overlapping_circles_xored(vec2 p, float n)
{
    ivec2 t = ivec2(floor(p*n));
    vec2 q = fract(p*n);

    return tile_env(t, q);
}

void mainImage(out vec4 o, in vec2 I)
{
    vec2 R = iResolution.xy;
    I = .5 + (I - R / 2.) / R.y;

    o = vec4(0);
    //if(all(lessThan(abs(I - 0.5), vec2(0.5))))
    {
        o = vec4(I.xy,0.0,1.0);

        o = vec4(vec3(random_overlapping_circles_xored(I.xy, 5.0)),1.0);
    }
}
