void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    vec4 color = texture(iChannel0, uv);
    fragColor = color;
}

vec3 getState(vec2 p) {
    if(iFrame < 10) {
        vec4 data = texture(
            iChannel0,
            p
        );
        return data.rgb;
    }
    vec4 data = texture(
        iChannel1,
        p
    );
    return data.rgb;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    float n = 0.0;
    n += getState((fragCoord + vec2(-1.0, -1.0)) / iResolution.xy).r;
    n += getState((fragCoord + vec2(0.0, -1.0)) / iResolution.xy).r;
    n += getState((fragCoord + vec2(1.0, -1.0)) / iResolution.xy).r;
    n += getState((fragCoord + vec2(-1.0, 0.0)) / iResolution.xy).r;
    n += getState((fragCoord + vec2(1.0, 0.0)) / iResolution.xy).r;
    n += getState((fragCoord + vec2(-1.0, 1.0)) / iResolution.xy).r;
    n += getState((fragCoord + vec2(0.0, 1.0)) / iResolution.xy).r;
    n += getState((fragCoord + vec2(1.0, 1.0)) / iResolution.xy).r;

    vec2 pos = 2.0 * fragCoord / iResolution.xy - 1.0;
    pos.x *= iResolution.x / iResolution.y;
    vec2 circle = vec2(cos(iTime) * 0.6, sin(iTime) * 0.6);
    float circleDist = length(pos - circle);

    if(circleDist < 0.1) {
        fragColor = vec4(1.0);
    } else {
        float live = getState(uv).r;
        if(n == 3.0) live = 1.0;
        else if(n > 3.0 || n <= 1.0) live = 0.0;
        // else live = step(0.8, live);
        fragColor = vec4(vec3(live), 1.0);
    }
}
