vec2 toCoordinate(vec2 xy)
{
    return 1.4 * (2.0 * xy.xy - iResolution.xy) / min(iResolution.x, iResolution.y);
}

void complexSquare(inout vec2 xy)
{
    xy = vec2(xy.x * xy.x - xy.y * xy.y, 2.0 * xy.x * xy.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = toCoordinate(fragCoord);
    vec2 uvIter = uv;

    vec2 c = (iMouse.z > 0.0)? toCoordinate(iMouse.xy) : uv;

    float iterations = 100.0;

    vec3 col = vec3(0.0);

    for (float i = 0.0; i < iterations; i++) {
        complexSquare(uvIter);
        uvIter += c;
        if (length(uvIter) > 2.0) {
            col = vec3(1.0 - i / iterations);
            break;
        }
    }


    fragColor = vec4(col, 1.0);
}
