//https://x.com/catzpaw/status/1530492124743221248
#define t iTime
#define r iResolution
#define FC gl_FragCoord

mat3 rotate3D(float angle, vec3 axis){
    vec3 a = normalize(axis);
    float s = sin(angle);
    float c = cos(angle);
    float r = 1.0 - c;
    return mat3(
        a.x * a.x * r + c,
        a.y * a.x * r + a.z * s,
        a.z * a.x * r - a.y * s,
        a.x * a.y * r - a.z * s,
        a.y * a.y * r + c,
        a.z * a.y * r + a.x * s,
        a.x * a.z * r + a.y * s,
        a.y * a.z * r - a.x * s,
        a.z * a.z * r + c
    );
}

void mainImage(out vec4 o, vec2 I)
{
    o *= 0.;
    float l,i,e;
    vec3 q,p=FC.qpp;
    for(p.xz-=t;i++<150.;o+=e/2e2)
    {
        p.xz=mod(p.xz+4.,8.)-4.;
        q=p+=normalize(FC.stp*2.-r.xyy)*e*.2;
        for(l=e=1.;l>.2;l*=.8)
            q=abs(q*1.2),
            e=min(e,max((q.y-=1.5)-.1,q.x+q.z-l*.2)),
            q*=rotate3D(l,FC.qqp-.75);
        e=min(e,p.y+q.z*.1);
    }
    o.gb-=q.y/i*FC.pq;
  //uncomment the following line to get more fun ;-)
  //o.x+=q.x;
}
