void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Coordenadas de textura normalizadas
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Textura original del canal iChannel0
    vec4 texColor = texture(iChannel0, uv);

    // Parámetros ajustables
    float lightSize = 0.7; // Tamaño del punto de luz (ajustable por uniforme)
    float lightIntensity = 1.5; // Intensidad del punto de luz
    float lightSpeed = 0.3; // Velocidad de movimiento del punto de luz

    // Posición del punto de luz (se mueve en diagonal a través de la pantalla)
    vec2 lightPosition = vec2(mod(iTime * lightSpeed, 1.0), mod(iTime * lightSpeed * 0.7, 1.0));

    // Calcula la distancia del fragmento al punto de luz
    float distToLight = distance(uv, lightPosition);

    // Calcula la intensidad del punto de luz basada en la distancia (efecto de punto de luz)
    float intensity = smoothstep(lightSize, lightSize * 1.5, 1.0 - distToLight);

    // Calcula el color del halo amarillo alrededor del punto de luz
    vec3 haloColor = vec3(1.0, 0.8, 0.5) * (1.0 - smoothstep(lightSize, lightSize * 1.5, 1.0 - distToLight));

    // Color final combinando fragColor y texColor con la luz y el halo
    fragColor = texColor + vec4(intensity * lightIntensity, intensity * lightIntensity, intensity * lightIntensity, 1.0);
    fragColor.rgb += haloColor * intensity;
}
