void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates and keeping aspect ratio
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    //calculating distance field centered at vec2(sin(iTime), cos(iTime))
    float d = length(abs(uv) - vec2(sin(iTime), cos(iTime)));

    //mixing between the two colors based on the fraction
    vec3 color = mix(vec3(1.0f, 0.0f, 1.0f), vec3(0.0f, 0.0f, 1.0f), (fract(d * 10. + 20. * sin(iTime))));

    fragColor = vec4(color, 1.0);
}
