void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalize pixel coordinates
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);

    // Time variables for animation
    float t = iTime * 0.5;

    // Parameters for the Mexican hat potential
    float mu = 2.0;
    float lambda = 1.0;

    // Calculate radius from center
    float r = length(uv);

    // Mexican hat potential V(φ) = -μ²|φ|²/2 + λ|φ|⁴/4
    float potential = -mu * mu * r * r / 2.0 + lambda * pow(r, 4.0) / 4.0;

    // Add field fluctuations
    float fluctuation = 0.0;
    for (int i = 1; i <= 5; i++) {
        float fi = float(i);
        fluctuation += 0.1 * sin(uv.x * fi * 3.0 + t) * sin(uv.y * fi * 2.0 + t * 1.5);
    }

    // Particle interaction visualization
    vec2 particlePos = vec2(
        0.3 * sin(t * 0.7),
        0.3 * cos(t * 0.9)
    );
    float particleInfluence = exp(-length(uv - particlePos) * 5.0);

    // Combine effects
    float field = potential + fluctuation;
    field += particleInfluence;

    // Color mapping
    vec3 color = vec3(0.0);

    // Base color from potential
    color += vec3(0.5 + 0.5 * sin(field * 3.0 + vec3(0.0, 2.0, 4.0)));

    // Add particle glow
    color += vec3(1.0, 0.8, 0.2) * particleInfluence;

    // Add symmetry breaking visualization
    float symBreak = smoothstep(-1.0, 1.0, sin(atan(uv.y, uv.x) * 2.0 + t));
    color += vec3(0.2, 0.3, 0.5) * symBreak * (1.0 - r);

    // Add vacuum expectation value circle
    float vev = smoothstep(0.05, 0.0, abs(r - mu/sqrt(lambda)));
    color += vec3(1.0) * vev;

    fragColor = vec4(color, 1.0);
}
