struct R{
   float v;
};
struct G{
   float v;
   R r;
};


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    /////////////////////////////////////////////////
    R r = R(1.0);
    G g = G(1.0,r);

    /////////////////////////////////////////////////
    fragColor = vec4(g.r.v,0.0,0.0,1.0);
}
