float squareColor(vec2 uv){
    uv = abs(uv);
    return ( pow(max(2.0*(0.5 - max(uv.x,uv.y)) , 0.0) , 0.2) ) ;
}

float squareColor2(vec2 uv){
    uv = abs(uv);
    if( 0.5 - max(uv.x,uv.y) > 0.0){return 1.0;}
    else return 0.0;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Normalized coordinates from -1 to 1
    vec2 uv = 2.0*(fragCoord/iResolution.xy - 0.5);
    uv.y *= iResolution.y/iResolution.x;
    vec2 uv1 = uv; vec2 uv2 = uv; vec2 uv3 = uv;
    int iterationAmount = 0;
    for (int i = 0; i < iterationAmount; i++){
        uv1 *= 2.0; uv1 += vec2(0.0 , 0.5);
        uv2 *= 2.0; uv2 += vec2(-0.5 , -0.5);
        uv3 *= 2.0; uv3 += vec2(0.5 , -0.5);
    }


    vec3 finalColor = vec3( clamp(squareColor(uv1) + squareColor(uv2) + squareColor(uv3), 0.0, 1.0) ) ;

    fragColor = vec4(finalColor, 1.0);
}
