#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))


void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,D,e; vec3 p,r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)*4.;
    p=vec3(log(D=length(p)),exp(p.y*i),atan(p.x,p.z));

    p.yx*=R2D(-iTime);

    for(j=0.;j++<7.;)
        p=abs(abs(p)-.05)-.04,       
        d=min(min(p.x,p.z),p.y),
        d-=D/1e4;

    e+=d;

}o+=vec4(exp(p*p*vec3(-1)),1)*5./i;
}
