void mainImage( out vec4 O, in vec2 U )
{
    O = texelFetch(iChannel0, ivec2(U), 0);
}

void mainImage( out vec4 O, in vec2 U )
{
    float scl = .03;
    vec2 V = U*scl;
    vec2 P = round(V);
    if ( iFrame < 5 ) {
        O = vec4( vec3( 1.-smoothstep(.35, .45, vec4(length(V-P)) )), 1. );
    } else {
        O = texelFetch(iChannel0, ivec2(U), 0);
        if ( iMouse.y == iMouse.w ) {
            vec2 m = iMouse.xy * scl;
            vec2 p = round(m);
            if ( P == p ) O *= 0.;
        }
    }
    if (O.x<=0.) O = texture(iChannel1, U/iResolution.xy);
}
