#define rotation(angle) mat2(cos(angle), -sin(angle), sin(angle), cos(angle));

float PI = 3.14159;
float TAU = 2.*3.14159;

// Thanks IQ!
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// Thanks IQ (Again!)
float sdPie( in vec2 p, in vec2 c, in float r )
{
    p.x = abs(p.x);
    float l = length(p) - r;
    float m = length(p-c*clamp(dot(p,c),0.0,r)); // c=sin/cos of aperture
    return max(l,m*sign(c.y*p.x-c.x*p.y));
}

// easings.net
float ease(in float x){
    return x < 0.5 ? 4. * x * x * x : 1. - pow(-2. * x + 2., 3.) / 2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = ( fragCoord - .5* iResolution.xy ) /iResolution.y;
   vec3 col = vec3(0.);   
   float tt = fract(.2*iTime);
   vec2 uv0 = uv;
   float scale = 2.;
   uv *= scale;
   vec2 cellID = floor(uv);
   uv = fract(uv) - .5;
   float rot = 0.;
   float w = 2./iResolution.y;
   if (cellID.x >= -1. && cellID.x <= 0.)
   if (cellID.y >= -1. && cellID.y <= 0.){
     float rr = PI + atan(cellID.y + .5, cellID.x + .5)+ PI*(abs(cellID.x + .5 + cellID.y + .5));
     if (tt <= .5){
       float ttt = 2.0 * tt;
       uv *= rotation(rr + TAU*ease(ttt));
       float b = sdPie(uv, vec2(PI/2.,-PI/2.), .4);
       col += smoothstep(w,-w,b);
     }
     else{
       float ttt = 2.0 * (tt - .5);
       uv0 *= rotation(4.*PI/2.*ease(ttt));
       float c = length(uv) - .4;
       float o = .05*pow(sin(PI*ttt),2.);
       float box = sdBox(uv0, vec2(.5/scale - o ,.5/scale - o));
       col += smoothstep(w,-w,c);
       col -= smoothstep(w/10.,-w/10.,box);
     }
   }
   fragColor = vec4(col,1.0);
}
