float circle(vec2 uv,float radius){
    float d=length(uv)-radius;
    return d;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     vec2 uv = (2.0*fragCoord-iResolution.xy - 1.0)/min(iResolution.y,iResolution.x);
    vec3 col =vec3(1.0);
    uv=fract(uv*2.0);
    uv-=0.5;
    float d=circle(uv,0.35);
    if(d>0.0){
        col=vec3(1.0);
        d+=iTime*0.05;

    }else {
        col=vec3(0.6,0.1,0.5);
         d-=iTime*0.05;
        col *= 1.0 - exp(-12.0*abs(d));
    }

     col*=0.8+sin(d*90.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
