void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize pixel coordinates
    vec2 uv = fragCoord / iResolution.xy;

    // Center the coordinates for radial effects
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // Time variable for animation
    float time = iTime * 0.08;

    // Base plasma effect
    float plasma = 0.5 + 0.5 * sin(uv.x * 12.0 + time) * cos(uv.y * 12.0 - time);
    plasma += 0.3 * sin(uv.y * 25.0 + time * 3.0); // Extra depth

    // Add dynamic shapes (circle and rectangle)
    float circle = smoothstep(0.3, 0.31, length(uv - vec2(0.2 * sin(time), 0.3 * cos(time))));
    float rectangle = smoothstep(0.3, 0.31, abs(uv.x + 0.2 * sin(time * 1.5)) + abs(uv.y - 0.3));

    // Combine shapes with plasma
    float shapes = circle + rectangle;
    plasma += shapes * 0.3; // Blend shapes into the plasma

    // Enhanced VHS glitch effect
    float glitch = sin(uv.y * 10.0 + time * 1.0); // Stronger horizontal distortion
    uv.x *= glitch;

    // Enhanced noise grain size
    float noise = fract(sin(dot(uv * 20.0, vec2(12.9898, 78.233))) * 43758.5453); // Larger grain
    plasma += noise * 0.2; // Add grain to plasma

    // Add cyan outline shape (dynamic circle outline)
    float outlineRadius = 0.2 + 0.05 * sin(time * 20.0); // Animated radius
    float outlineWidth = 0.2; // Thickness of the outline
    float distanceFromCenter = length(uv - vec2(.0, .0)); // Distance from center
    float cyanShape = smoothstep(0.5, outlineRadius - outlineWidth, distanceFromCenter);

    // Combine the black-and-white plasma effect with the cyan shape
    float col = plasma - (glitch * 0.5);
    vec3 finalColor = vec3(1.0 - col); // Black-and-white base
    finalColor = mix(finalColor, vec3(0.0, 1.0, 1.0), cyanShape - noise * 0.1); // Add cyan to outline

    fragColor = vec4(finalColor, 1.0);
}
