#define I iResolution

#define clip( a, b, u ) length( u - (a+b) * .5 ) - length( a-b )

float mExp( vec2 p1, vec2 p2, in vec2 uv ){
    return (uv.y - p2.y) / (p1.y-p2.y) - (uv.x - p2.x) / (p1.x-p2.x);
}

float r( vec2 u ){
    float p;
    float c;
    vec2 a, b;

    p = length( u + vec2( 0, -.2 ) ) - .05;

    c += smoothstep( .1, p, .103 );

    p = max(
        abs( u.x ) - .005,
        abs( u.y + .15 ) - .2
    );

    c += step( p, 0. );

    a = vec2( 0.06, -.1 ),
    b = vec2( 0.2, -0.2 );

    p = mExp( a, b, u );

    c += smoothstep( 0.11, p, 0.15 )  * step( clip( a, b, u ), 0. );


    a = vec2( -0.06, -.1 ),
    b = vec2( -0.2, -0.2 );

    p = mExp( a, b, u );

    c += smoothstep( 0.11, p, 0.15 )  * step( clip( a, b, u ), 0. );


    a = vec2( -0.04, -.44 ),
    b = vec2( -0.15, -0.6 );

    p = mExp( a, b, u );

    c += smoothstep( 0.11, p, 0.15 )  * step( clip( a, b, u ), 0. );


    a = vec2( 0.04, -.44 ),
    b = vec2( 0.15, -0.6 );

    p = mExp( a, b, u );

    c += smoothstep( 0.11, p, 0.15 )  * step( clip( a, b, u ), 0. );

    return c;
}

void mainImage( out vec4 U, in vec2 V )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 u = ( V * 2. - I.xy ) / I.y;

    // Time varying pixel color
    vec3 c;


    for( float i = -1.; i < 2.; i++ ){
       c += r( u * cos(i * .7 ) / .7 - vec2(1, cos(i) * .2 ) * .55 * i );
    }

    // Output to screen
    U = vec4(c,1.0);
}
