float layer(vec2 uv, vec2 pos, float r, float blur){
    float d = length(uv-pos);
    d = smoothstep(r, r-blur, d);
    return d;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.* fragCoord - iResolution.xy) / iResolution.y;
    float fig1 = layer(uv, vec2(0.), 0.8, 0.795);
    float fig2 = layer(uv, vec2(0.), 1., 0.995);
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*sin(uv.xyx+vec3(0,1,8));
    col *= (fig1+fig2);
    // Output to screen
    fragColor = vec4(col,1.0);
}
