float rotCircle(float r,vec2 pos,vec2 uv,float speed){

    speed = -speed;
    pos.x = -pos.y*sin(speed);
    pos.y = cos(speed)*pos.y;

    float circle = length(uv - pos);
    circle = smoothstep(r,r+0.01,circle);
    return circle;

}
vec3 palette(float t){
    vec3 a = vec3(0.5,0.5,1.0);
    vec3 b = vec3(0.5,0.5,1.0);
    vec3 c = vec3(0.5,0.5,1.);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos(6.28318*(c*t + d));

}
vec4 glow(vec2 fragCoord)
{  
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
    vec2 uv0 =uv;
    vec3 finalColor = vec3(0.);
    for(float i =0.0;i<8.0;i++)
    {
        uv = fract(1.5*uv)-0.5; 
        float d=length(uv)* exp(-length(uv0)) ;
        vec3 col = palette(length(uv0) + i * iTime*0.2);
        d = sin(d*8. + iTime); 
        d = abs(d);
        d = pow(0.2/d,1.2); 
        finalColor += col*d;
    }
    return vec4(finalColor,1.);      

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    float speed = iTime*5.;
    vec2 uv0 =fragCoord/iResolution.xy;
    vec2 uv =(2.0*fragCoord - iResolution.xy)/iResolution.y;

    float circle = length(uv)-0.4;
    float smoothCircle = smoothstep(0.01,0.,circle);
    float outedge =clamp ( 0.03/clamp(circle,0.,1.),0.,1.) - smoothCircle;

    float rotuvx = uv.x*cos(speed) -uv.y*sin(speed);
    float edgeuv = step(0.,rotuvx);

    float circle1 = rotCircle(0.2,vec2(0.,.205),uv,speed) *edgeuv ;
          circle1 = clamp(circle1,0.,1.);
    float circle2 = rotCircle(0.2,vec2(0.,-.205),uv,speed);
          circle2 = (1.-circle2) *(1.-edgeuv);
          circle2 = clamp(circle2,0.,1.);
    float circle3 = rotCircle(0.1,vec2(0.,.205),uv,speed);
          circle3 = 1.-circle3;
    float circle4 = rotCircle(0.1,vec2(0.,-.205),uv,speed);
          circle4 = 1.-circle4;
    vec4 taiji=  vec4(circle1 + circle2 +  circle3  - circle4); 

    fragColor = taiji * smoothCircle + pow(outedge,1.5)*0.2 + glow(fragCoord)*outedge; 
}
