#define pi 3.1415926
#define tau (pi+pi)

bool inside_regular_polygon(int n, vec2 p) // https://www.shadertoy.com/view/l3fBR8 regular polygon distance (jt)
{
    float slice = tau/float(n);
    float mu = (floor(atan(p.x,p.y)/slice)+0.5)*slice;
    vec2 q = vec2(sin(mu),cos(mu));
    float r = cos(slice / 2.0); 
    return dot(p, q) < r;
}

float line(vec2 p, vec2 a, vec2 b) // https://iquilezles.org/articles/distfunctions2d/
{
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa,ba)/dot(ba,ba), 0.0, 1.0);
    //return length(pa - ba*h); // original (iq)
    return length(pa - ba*h)*sign(ba.x*pa.y-ba.y*pa.x); // thanks @DjinnKahn for filled interior
    //return length(pa - ba*h)*sign(determinant(mat2(ba,pa))); // also filled interior (jt)
}
