float regular_polygon(int n, vec2 p) // https://www.shadertoy.com/view/l3fBR8 regular polygon distance (jt)
{
    float slice = tau/float(n);

    float mu = atan(p.x,p.y);
    float mu0 = floor(mu/slice)*slice;
    float mu1 = mu0+slice;
    vec2 q0 = vec2(sin(mu0),cos(mu0));
    vec2 q1 = vec2(sin(mu1),cos(mu1));
    return line(p,q0,q1);
}
