float map(vec2 p)
{
    float s = 1.2;
    int n = int(mix(3.0, 25.0, 0.5+0.5*cos(2.0*pi*iTime/10.0))); // animate number of leafs
    return regular_polygon(n, p*s)/s;
    return min(regular_polygon(n, p*s)/s,abs(length(p)-1.0/s)); // poligon fits inside circle of radius s
}

#define EPSILON 0.001

// part of sdf error detection (jt/spalmer)
vec2 gradient(vec2 p) // NOT normalized so we can use it's length to detect errors in the SDF.
{
    vec2 h = vec2(EPSILON,0);
    return (vec2(map(p+h.xy) - map(p-h.xy),
                 map(p+h.yx) - map(p-h.yx)))/EPSILON/2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    float d = map(p);

    // iq's coloring
    vec3 col = (d<0.0) ? vec3(0.6,0.8,1.0) : vec3(0.9,0.6,0.3);
    col *= 1.0 - exp(-9.0*abs(d));
    col *= 1.0 + 0.2*cos(128.0*abs(d));
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );
    // iq's mouse distance visualization
    if( iMouse.z>0.001 )
    {
        vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
        d = map(m);
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
        col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

    // sdf error detection (jt/spalmer)
    {
        // Jump the distance given by the SDF at the current position
        // along the gradient given by the SDF at the current position
        // (direction to the nearest surface) and evaluate the SDF there.
        // Because the SDF is supposed to describe the minimal distance to the surface,
        // the result should be (close to) zero. (translation from math to words: jt)
        float error = map(p - gradient(p) * map(p)); // SDF error detection suggested to me by spalmer - thanks!
        //col += smoothstep(0.0,sqrt(EPSILON), abs(error)) * (cos(iTime*2.0*pi)*0.5+0.5); // detect both interior and exterior errors (NOTE: false alarms on some shapes)
        col.r += smoothstep(0.0,/*sqrt*/(EPSILON), max(0.0,+error)) * (cos(iTime*2.0*pi)*0.5+0.5); // detect both interior and exterior errors (NOTE: false alarms on some shapes)
        col.g += smoothstep(0.0,/*sqrt*/(EPSILON), max(0.0,-error)) * (cos(iTime*2.0*pi)*0.5+0.5); // detect both interior and exterior errors (NOTE: false alarms on some shapes)
    }

    // output
    fragColor = vec4(col, 1.0);
}
