vec3 palette( in float t, vec3 a, vec3 b, vec3 c, vec3 d)
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    //Setup screenspace
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    //Initialise varaibles
    vec2 uv0 = uv;
    vec3 finalcolor = vec3(0.0);
    float col = 0.0;
    vec3 color = vec3(0.0);    

    //Split up canvas
    uv = fract(uv * 2.9) - 0.5;

    //Make circles
    float r = 0.3;
    float smoothness = 50.0 / iResolution.x;
    float distFromCenter = length(uv);
    col = smoothstep(r, r + smoothness, distFromCenter);

    //Add rainbow effect
    float xCol = (1.5*sin(uv0.x*2.0))+1.4;
    float yCol = (1.5*sin(uv0.y*2.0))+1.4;  
    color = palette(xCol + yCol + iTime/3.0, vec3(0.5, 0.5, 0.5), vec3(0.5, 0.5, 0.5), vec3(1.0, 1.0, 1.0), vec3(0.0, 0.333, 0.666));

    // Output to screen
    finalcolor = vec3(col/color);
    fragColor = vec4(finalcolor, 1.0);
}
