uvec2 bitreverseinterleave16( uvec2 up )
{
    up = ( ( up &     0xff00u ) >> 8u ) | ( ( up &     0x00ffu ) << 16u );
    up = ( ( up &   0xf000f0u ) >> 4u ) | ( ( up &    0xf000fu ) <<  8u );
    up = ( ( up &  0xc0c0c0cu ) >> 2u ) | ( ( up &  0x3030303u ) <<  4u );
    up = ( ( up & 0x22222222u ) >> 1u ) | ( ( up & 0x11111111u ) <<  2u );
    return up;
}

float ditherthres( vec2 p )
{
    uvec2 up = bitreverseinterleave16( uvec2( p ) );
    return float( up.x + 2u * ( up.x ^ up.y ) ) / 4294967296.;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    float grey = iMouse.z > 0. ? (
        uv.y < .375 ? pow( uv.x, 2.2 ) >= ditherthres( fragCoord ) ? 1. : 0. :
        uv.y < .625 ? uv.x :
                     ( uv.x < 0.04045 ? uv.x / 12.92 : pow( ( uv.x + .055 ) / 1.055, 2.4 ) ) >= ditherthres( fragCoord ) ? 1. : 0.
    ) : (
        uv.y < .375 ? pow( round( uv.x * 10. ) / 10., 2.2 ) >= ditherthres( fragCoord ) ? 10. / 255. : 0. :
        uv.y < .625 ? round( uv.x * 10. ) / 255. :        
                      round( uv.x * 10. ) / 10. >= ditherthres( fragCoord ) ? 10. / 255. : 0.
    );

    fragColor = vec4( vec3( grey ), 1 );        
}
