void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 red = vec3(1, 0, 0);
    vec3 green = vec3(0, 1, 0);
    vec3 yellow = vec3(1, 1, 0);
    vec3 blue = vec3(0, 0, 1);

    // check the non-const version
    float t1 = mod(floor(iTime), 2.0);
    float t2 = 1.0 - t1;

    vec3 s0 = mix(red, green, smoothstep(t1, t2, uv.x));
    vec3 s1 = mix(red, green, smoothstep(t2, t1, uv.x));

    // const version
    vec3 s2 = mix(yellow, blue, smoothstep(0.0, 1.0, uv.x));
    vec3 s3 = mix(yellow, blue, smoothstep(1.0, 0.0, uv.x));

    vec3 top = mix(s0, s1, step(0.75, uv.y));
    vec3 bot = mix(s2, s3, step(0.25, uv.y));

    vec3 col = mix(bot, top, step(0.5, uv.y));

    fragColor = vec4(col,1.0);
}
