float getlight(vec3 color){
    return dot(color,vec3(0.21,0.71,0.08));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // IIIIIIIIIIIIIIIIIIIIIIIIIIIIII

    // Hey there, try different numbers here :) like 2. or 6. (compile by pressing alt+enter)
    float colors = 4.;

    // IIIIIIIIIIIIIIIIIIIIIIIIIIIIII
    vec3 col = texture(iChannel0,uv).xyz;
    float odd = float(mod(
        floor((uv.x)*iResolution.x/3.5)+floor((uv.y)*iResolution.y/3.)
        ,2.) >= 1.);


    col = vec3(
        floor(getlight(col + (.33/colors)*odd)*colors)/colors
    );


    fragColor = vec4(col,1.0);
}
