float scale = 30.;
float cutoff = 0.25;
float sharpness = 0.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv = (uv-0.5) * vec2(iResolution.x/iResolution.y, 1) + 0.5;
    vec3 gamma = texture(iChannel0, floor(uv*scale)/scale).xyz;
    float br = 0.2126 * gamma.r + 0.7152 * gamma.g + 0.0722 * gamma.b;
    float c = smoothstep(0.5, 0.5 * sharpness, length(fract(uv*scale)-0.5));
    c = 1. - step(cutoff, br * c);
    fragColor = vec4(c,c,c,1.0);
}
