void mainImage( out vec4 o, vec2 i )
{
    vec2 uv = i/iResolution.xy, uv1 = mod(uv, uv.x/uv.y), uv2 = mod(uv, uv.y/uv.x);
    o = vec4(
        mix(
            (vec3(.8,.5,.4)+vec3(.2,.4,.2)*cos(6.38*(vec3(2.,1.,1.)*sin(iTime*1.1+length(mix(uv1, uv2, sin(iTime)*.5+.5))*2.)*.5+.5+vec3(.0,.3,.3))))*vec3(uv, sin(iTime*1.2)*.5+.5)
            ,vec3(0.),
            length(uv1-uv2)-.5)
        ,1.);
}
