Main {
    vec2 u = U;
    #define dt 2.
    if (iFrame%5==0) {
        for (int i = 0; i < 15; i++) {
            vec4 v = B(U);
            U -= dt*v.xy*ei(v.z);
        }
    }
    Q.xyz = A(U).xyz;
    if (iFrame%10==0) {
        for (int i = 0; i < 30; i++) {
            vec4 v = B(u);
            u -= dt*v.xy*ei(v.z);
        }
    }
    Q.w = A(u).w;
    Q.y += 5e-3*Q.w;
    Q.xy *= .9;
    if (iFrame < 1) {

        Q.w = -sign(U.y-.3*R.y+3.*sin(.1*U.x));
    }
