float circle(vec2 fragCoord, vec2 center, float radius){

    return smoothstep(1.0,0.5,sqrt((fragCoord.x-center.x)*(fragCoord.x-center.x)+(fragCoord.y-center.y)*(fragCoord.y-center.y))/radius);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float pct = circle(fragCoord, vec2(-100.0+mod(iTime*200.0,iResolution.x+200.0),iResolution.y/2.0+50.0*sin(iTime)), 100.0) +
                circle(fragCoord, vec2(-100.0+mod(iTime*150.0,iResolution.x+200.0),iResolution.y/2.0+80.0), 100.0)+
                circle(fragCoord, vec2(-100.0+mod(iTime*180.0,iResolution.x+200.0),iResolution.y/2.0-40.0), 100.0)+
                circle(fragCoord, vec2(-100.0+mod(iTime*100.0,iResolution.x+200.0),iResolution.y/2.0-100.0+30.0*sin(iTime*.5)), 100.0) +
                circle(fragCoord, vec2(-100.0+mod(iTime*130.0,iResolution.x+200.0),iResolution.y/2.0+150.0), 100.0)+
                circle(fragCoord, vec2(-100.0+mod(iTime*190.0,iResolution.x+200.0),iResolution.y/2.0+34.0), 100.0)+
                circle(fragCoord, vec2(-100.0+mod(iTime*220.0,iResolution.x+200.0),iResolution.y/2.0), 100.0) +
                circle(fragCoord, vec2(-100.0+mod(iTime*10.0,iResolution.x+200.0),iResolution.y/2.0+80.0+100.0*sin(iTime*.3)), 100.0)+
                circle(fragCoord, vec2(-100.0+mod(iTime*300.0,iResolution.x+200.0),iResolution.y/2.0-120.0), 100.0)+
                circle(fragCoord, vec2(-100.0+mod(iTime*40.0,iResolution.x+200.0),iResolution.y/2.0-200.0), 100.0) +
                circle(fragCoord, vec2(-100.0+mod(iTime*51.0,iResolution.x+200.0),iResolution.y/2.0+170.0), 100.0)+
                circle(fragCoord, vec2(-100.0+mod(iTime*30.0,iResolution.x+200.0),iResolution.y/2.0+34.0), 100.0);

   // Time varying pixel color
    vec3 background = vec3(.3,.1,.5);
    vec3 foreground = vec3(0.75,0.0,1.0);
    vec3 col = pct*foreground + (1.0-pct)*background;

    // Output to screen
    fragColor = vec4(col,1.0);
}
