#define n         9.
#define mul(a,b)  mat2(a, -a.y, a.x) * (b)  // by deMoivre
#define inv(a)    vec2( (a).x, -(a).y ) / length(a)

void mainImage( out vec4 color, in vec2 coord ) {
    vec2 r = iResolution.xy,
        uv = (2. * coord - r) / r.y,
         z = vec2(1., 0.);

    for ( float i = 0.; i < 2. * n; i++ ) {
        float t = 3.14159 * pow( i / n, 1.7 ) - .1 * iTime;
        vec2 p = vec2(
            sin(2.1 * t) - sin(3.7 * t),
            .6 * ( cos(3.3 * t) - sin(2.3 * t) )
        );
        z = inv( mul( z, uv - p ) );
    }

    color.rgb = .5 + .5 * cos( iTime + z.xyx + vec3(0, 2, 4) );
}
