vec3 Circle(vec2 uv, vec2 p, float size)
{
    float d = distance(p, uv) * 2.;
    float s = smoothstep(size, size + 0.01, d);
    return vec3(1. - s);
}

vec3 Ring(vec2 uv, float size)
{
    vec2 p = vec2(0.875, 0.5);
    p.x += sin((iTime* 6.) + uv.y * 100.) * 0.025;

    vec3 outer = Circle(uv, p,size);
    vec3 inner = Circle(uv, p,size - 0.05);

    return outer - inner;
}

vec3 DancingOrbs(vec2 uv)
{
    vec3 o;

    for(float i = 2.; i <= 16.;i++)
    {
        float x = (1. + cos(iTime * log2(i) * 0.5)) * 0.5;
        float y = (1. + sin(iTime * log2(i) * 0.5)) * 0.5;
        vec3 c1 = Circle(uv, vec2(x + 1.75, y + 0.5) * 0.5, i * 0.015);
        vec3 m = mix(vec3(0.53,0.1,y), vec3(0.729,0.23,x), uv.x);
        c1 *= m;

        vec3 c2 = Circle(uv, vec2(-x + 1.75, y + 0.5) * 0.5,i * 0.015);
        m = mix(vec3(0.53,0.1,y), vec3(0.729,0.23,x), uv.x);
        c2 *= m;
        o += (c1 + c2);
    }

    return o;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float r = iResolution.x / iResolution.y;
    uv.x *= r;   

    float f = fract(iTime * 0.25);

    vec3 orbs = DancingOrbs(uv);
    vec3 ring = Ring(uv, f * 1.0) + Ring(uv,f * 0.3) + Ring(uv,f * 0.66);
    vec3 o = orbs + orbs * mix(ring, vec3(0.), f);

    fragColor = vec4(o,1.0);
}
