Main {
    Q = vec4(0);
    for(float Z = 5.; Z  <= 20.; Z+=3.) {
        vec2 V = (1.5+sin(.05*iTime))*Z*(U-.5*R)/R.y;
        V.y -= iTime+4.;
        vec2 u = vec2(1e9);
        for (float x = -1.; x <= 1.; x++)
        for (float y = -1.; y <= 1.; y++)
        {
            vec2 v = round(V)+vec2(x,y);
            vec4 h = hash44(vec4(v,Z,10));
            v += .25*(h.xy*2.-1.);
            v += .25*vec2(cos(iTime*4.*(h.w-.5)+h.z),sin(iTime*2.*(h.w-.5)+h.z));
            if (length(V-v)<length(V-u)) u = v;
        }
        vec2 u2 = vec2(1e9);
        for (float x = -1.; x <= 1.; x++)
        for (float y = -1.; y <= 1.; y++)
        {
            vec2 v = round(u)+vec2(x,y);
            vec4 h = hash44(vec4(v,Z,10));
            v += .25*(h.xy*2.-1.);
            v += .25*vec2(cos(iTime*4.*(h.w-.5)+h.z),sin(iTime*2.*(h.w-.5)+h.z));
            if (length(v-u)<length(u2-u)&&length(v-u)>0.) u2 = v;
        }
        float r = .5*length(u-u2);
        float l = length(V-u)-r;
        float d = dot(V-u,V-u);
        float h = sqrt(r*r-d);

        vec3 p = vec3(V,h);
        vec3 p1 = vec3(V,-h);
        vec3 m = vec3(0,-iTime,Z);
        vec3 c = vec3(u,0);
        vec3 n = normalize(p-c);
        vec3 n1 = normalize(p1-c);
        vec3 rfl = reflect(normalize(p-m),n);
        vec3 rfr = reflect(normalize(p1-m),n1);
        if (d<=r*r) Q.xyz += exp(-3.*length(Q))*vec3(1,1,1)*(
            1.*(.5+rfl.xzy)*texture(iChannel1,rfl).xyz+
            1.*(.5+rfr)*texture(iChannel1,-rfr).xyz)*exp(-.1*Z);
      }
      Q += .1;
}

#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U) 
#define A(U) texture(iChannel0,(U)/R)
vec4 hash44(vec4 p4)
{
    p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
