// Fork of "2d shadows 2" by GRaAL. https://shadertoy.com/view/llyXD1
// +less steps
// +some fixes

#define MAX_STEPS 100
#define STEP_DIV MAX_STEPS
#define raycast raycastMath


bool isBlack(vec4 color) {
    if (color.r + color.b + color.g < 20./255.) {
        return true;
    }
    return false;
}


float getBlackness(vec4 color) {
    return step(20./155., color.r * color.b * color.g);
}

float raycastMath(vec2 uv, vec2 light) {

  	vec2 stp = (light - uv) / float(STEP_DIV);
    vec2 point = uv;
    float res = 1.;
    for (int i = 0; i < MAX_STEPS; i++) {
        res = min(res, 0.5 + 0.5*getBlackness(texture(iChannel0, point)));
        point += stp;
    }
  	return res;
}

float lighting(vec2 uv, vec2 light) {
  	return pow(1. - min(0.2, distance(uv, light)), 2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 light = vec2(0.46+0.9*cos(iTime)/2., 0.5 + 0.9*sin(iTime)/2.);
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv) * raycast(uv, light) * lighting(uv, light);
}
