#define PI 3.14159265359

vec3 colorA = vec3(0.306,0.445,0.912);
vec3 colorB = vec3(1.000,0.508,0.224);
vec3 colorC = vec3(0.007,0.115,0.300);
vec3 colorSun = vec3(0.985,0.478,0.544);

float plot(vec2 st, float pct){
  return  smoothstep( pct-0.1, pct, st.y) -
          smoothstep( pct, pct+0.1, st.y);
}

float easeOutHalfQuart(float x) {
	return 1.0 - pow(1.0 - x, 2.0);
}

float halfQuad(float x) {
    return pow(cos(x * PI / 2.0), 2.5);
}

float easeInSine(float x) {
	return 1.0 - cos((x * PI) / 2.0);
}

vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uv) - rad;
	float t = clamp(d, 0.0, 1.0);
	return vec4(color, 1.0 - t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);
    vec3 pct = vec3(st.y);
	vec2 uv = gl_FragCoord.xy;
	vec2 center = vec2(iResolution.x * 0.93, iResolution.y * 0.45);
	float radius = 0.02 * iResolution.y;

    pct.r = easeOutHalfQuart(0.5-st.x) * smoothstep(0.8, 1.0, st.y);
    pct.b = easeInSine(st.y);
    pct.g = 0.0;
    color = mix(colorB, colorA, pct * abs(sin(iTime/PI)));

    pct = vec3(halfQuad(st.y)) * smoothstep(0.0, 0.4, st.y);
    color = mix(color, colorA, pct);

    pct = vec3(halfQuad(st.y)) * smoothstep(0., 0.2, st.y) * smoothstep(1.5, 0.0, st.x);
    color = mix(color, colorC, pct * abs(sin(iTime/PI)));

    pct = vec3(plot(st, 0.25) * 0.3);
    color = mix(color, colorC/2.0, pct);

    vec4 layer1 = vec4(color, 1.0);
    vec4 layer2 = circle(uv, center, radius, colorSun);

    fragColor = mix(layer1, layer2, layer2.a);

}
