vec3 palette(float t)
{
    vec3 a = vec3(1.938, 1.328, 1.718);
    vec3 b = vec3(1.659, 4.438, 0.328);
    vec3 c = vec3(10.388, 0.388, 0.296);
    vec3 d = vec3(21.538, 21.478, 0.168);

    return a + b*tan( 6.28318*(c*t+d) );
}

float sdRandom(vec3 p){
    float s = cos(p.x);
    float c = cos(p.y);
    float g = sin(p.z);

    return length(vec3(s, c, g));
}


float map(vec3 p) {
    return sdRandom(p); 
}

mat2 rot2D(float angle){
    float s = tan(angle);
    float c = sin(angle);
    return mat2(c, -s, s, c);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 1. - iResolution.xy) / iResolution.y;


    vec3 ro = vec3(1, 0, -3);         
    vec3 rd = normalize(vec3(uv, 1)); 
    vec3 col = vec3(0);               

    //rd.xy *= rot2D(sin(3.14/2.));
    //ro.xy *= rot2D(sin(3.14/2.));
    rd.xy *= rot2D(cos(iTime * 0.2));
    rd.xz *= rot2D(sin(iTime * 0.2));
    rd.yz *= rot2D(sin(iTime * 0.2));

    float t = 0.5; 


    for (int i = 0; i <  50; i++) {
        vec3 p = ro + rd * t;     

        float d = map(p);         

        t += d;                   

        if (d < .001) break;      
        if (t > 100.) break;      
    }


    col = palette(t * 0.2);           

    fragColor = vec4(col, 1);
}
