float p(float base) {
   return sin(base + iTime) * 0.5 + .5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 a = vec2(p(0.5), p(0.9));
    vec2 b = vec2(p(0.1), p(0.1));
    vec2 c = vec2(p(0.9), p(0.1));

    vec2 p = uv;

    vec2 ab = b-a;
    vec2 bc = c-b;
    vec2 ca = a-c;

    vec2 abf = vec2(ab.y,-ab.x);
    vec2 bcf = vec2(bc.y,-bc.x);
    vec2 caf = vec2(ca.y,-ca.x);

    vec2 ap = p-a;
    vec2 bp = p-b;
    vec2 cp = p-c;

    float va = dot(abf, ap);
    float vb = dot(bcf, bp);
    float vc = dot(caf, cp);


    float triangle = 0.;

    if(va>0.&&vb>0.&&vc>0.) {
        triangle = 1.;
    }

    if(va<0.&&vb<0.&&vc<0.) {
        triangle = 1.;
    }

    col = col * triangle;


    fragColor = vec4(col,1.0);
}
