void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_trn = (fragCoord.xy)/iResolution.y;


    vec2 o_trn_m = (iMouse.xy)/iResolution.y;
    if(iMouse.z == 0.){
        o_trn_m = vec2(sin(iTime), cos(iTime));
    }
    vec2 o_p = o_trn_m;

    // this is actually the hadamard product 
    vec2 o_hadamard_product = o_trn*o_p;
    // it is the same as , it is component wise multiplication
    vec2 o_hadamard_product_manual = vec2(
        o_trn.x * o_p.x, 
        o_trn.y * o_p.y
    );
    // it can also be expressed as a linear combination
    vec2 o_xone = vec2(1., 0.);
    vec2 o_yone = vec2(0., 1.);


    vec4 o_col = texture(
        iChannel0, 
        o_hadamard_product_manual
    );

    fragColor = vec4(
        o_col
    );
}
