void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    float squareSize = 1.0;

    vec2 bufferUv = (uv * 3.0 - 0.1) * squareSize;

    fragColor = texture(iChannel0, bufferUv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/ iResolution.xy;

  vec3 color1 = vec3(0.05, 0.79, 0.94);
  vec3 color2 = vec3(1.0, 1.0, 0.05);

  float diagonal = uv.x + uv.y;

  fragColor = vec4(mix(color1, color2, smoothstep(1.0, 1.0, diagonal)), 1.0);
}
