// Facebook: https://www.facebook.com/groups/theartofcode/
//
// You can use this shader as a template for ray marching shaders

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define TAU 6.283185
#define PI 3.141592
#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}


float GetDist(vec3 p) {

    p.xz *= Rot(iTime * .1);

    float d = sdBox(p, vec3(1));

    // As if folding a piece of paper we'll nede the line along we'll fold and the direction and the angle
    float c = cos(3.1415 / 5.); // angle
    float s = sqrt(0.75 - c*c);
    vec3 n = vec3(-0.5, -c, s); // direction

    p = abs(p); // standard
    p -= 2. * min(0., dot(p, n)) * n; //  the folding

    p.xy = abs(p.xy);
    p -= 2. * min(0., dot(p, n)) * n;

    p.xy = abs(p.xy);
    p -= 2. * min(0., dot(p, n)) * n;

    d = p.z - 1.;
    return d;
}

float RayMarch(vec3 ro, vec3 rd, float side) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p) * side;
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.01, 0);
    vec3 n = GetDist(p) - 
        vec3(GetDist(p-e.xyy), GetDist(p-e.yxy),GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 
        f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u;
    return normalize(i);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*PI+1.);
    ro.xz *= Rot(-m.x*TAU);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 1.);
    vec3 col = texture(iChannel0, rd).rgb;

    float d = RayMarch(ro, rd, 1.); // Outside of object

    float IOR = 1.45; // Index Of Refraction: the denser the material, the higher it gets
    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;    // 3D hit position
        vec3 n = GetNormal(p);   // normal: orientation of surface
        vec3 r = reflect(rd, n);
        vec3 reflOutside = texture(iChannel0, r).rgb; // outside reflection

        vec3 refrDirIn = refract(rd, n, 1./IOR); // Refracting with native function passing through IOR

        vec3 pEnter = p - n*SURF_DIST * 3.;
        float dIn = RayMarch(pEnter, refrDirIn, -1.); // inside the object

        vec3 pExit = pEnter + refrDirIn * dIn;
        vec3 nExit = -GetNormal(pExit);

        vec3 reflTex = vec3(0);
        vec3 refrDirOut = vec3(0);

        float abb = .01; // Chromatic Aberration

        // Separate three canals
        // RED
        refrDirOut = refract(refrDirIn, nExit, IOR - abb);
        if (dot(refrDirOut, refrDirOut) == 0.) refrDirOut = reflect(refrDirIn, nExit);
        reflTex.r = texture(iChannel0, refrDirOut).r;

        // GREEN
        refrDirOut = refract(refrDirIn, nExit, IOR);
        if (dot(refrDirOut, refrDirOut) == 0.) refrDirOut = reflect(refrDirIn, nExit);
        reflTex.g = texture(iChannel0, refrDirOut).g;

        // BLUE
        refrDirOut = refract(refrDirIn, nExit, IOR + abb);
        if (dot(refrDirOut, refrDirOut) == 0.) refrDirOut = reflect(refrDirIn, nExit);
        reflTex.b = texture(iChannel0, refrDirOut).b;

        float dens = 0.5;
        float optDist = exp(-dIn * dens);

        //vec3 sapphireColor = vec3(1., 0.592, 0.8);
        //vec3 coloredDens = (1. - log(sapphireColor)) * dens;
        //vec3 optDist = exp(-dIn * coloredDens);
        reflTex = reflTex * optDist;

        float fresnel = pow(1. + dot(rd, n), 5.);
        col = mix(reflTex, reflOutside, fresnel);
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}
