#define T iTime
#define R iResolution
#define M_PI 3.141592653

vec3 color_palette(float dist) {
    vec3 base = vec3(1,2,3);
    base = base + vec3(sin(T + dist), cos(T + dist * 0.5), sin(T + dist * 2.0));
    return base;
}

void mainImage( out vec4 fragColor, in vec2 coord ) {
    vec2 uv = coord/R.xy;
    vec2 uvc = (coord-R.xy/2.)/R.y * (7. + 0.5*sin(T));
    uvc.y += .2;
    float d;
    vec3 col;
    for (float a = 1.; a < 20.5; ++a) {
        d = pow(sin(M_PI * a / 10.) + uvc.x, 2.) + pow(cos(M_PI * a / 10.) + uvc.y, 2.) - 0.7 * uvc.y * abs(uvc.x) - 1.5 + sin(T);
        col += smoothstep(0.45, 0.55, d) * smoothstep(0.55, 0.45, d) * color_palette(d) * 2.0;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
