float random (float x) {
    return fract(sin(x)*1e4);
}

vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))))*43758.5453);
}

float sdSegment(vec2 st, vec2 a, vec2 b )
{
    vec2 pa = st-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord.xy * 2. - iResolution.xy) / iResolution.y; 

    vec3 col = vec3(0.);    
    float coef = 2.0;
    vec2 st0 = st;
    st *= coef;    
    st.y -= iTime * coef;
    st.x += sin(iTime * 2.0 * random(0.1));

    vec2 i_st = floor(st);
    vec2 f_st = fract(st);    

    float m_dist = 1.;

    for (int y= -1; y <= 1; y++) {
        for (int x= -1; x <= 1; x++) {

            vec2 neighbor = vec2(float(x) ,float(y));  

            vec2 point = random2(i_st + neighbor);            
            float sinTiming = 0.5 + 0.5*sin(iTime * 5. + 6.2831);
            float cosTiming = 0.5 + 0.5*cos(iTime * 5. + 6.2831);
            point = 0.5 + 0.5*sin(iTime * 5. + 6.2831*point);

            vec2 diff = neighbor + point - f_st;

            float dist = sdSegment(diff, vec2(0.2), vec2(sinTiming * abs((0.5-point.x)), cosTiming * 0.1));

            m_dist = min(m_dist, dist);
        }
    }

    m_dist = 0.01 / m_dist;
    m_dist = abs(m_dist); 
    col += m_dist;
    st0 *= 1.5;   
    st0 = cos(st0);
    col *= vec3(1.5 * cos(st0.y), 0.3, 0.1) * st0.x;       


    fragColor = vec4(col,1.0);
}
