float min2(vec2 xy)
{
    return min(xy.x,xy.y);
}

float rect(float edge1, float edge2, float t, float smoot){
    smoot = smoot/2.;
    return smoothstep(edge1-smoot,edge1+smoot,t)*(1.-smoothstep(edge2-smoot,edge2+smoot,t));
}

float grid(vec2 uv, float freq, float thickness)
{
    return 1.-float(min2(mod(uv,1./freq)) < thickness);
}

float smoothBox(vec2 uv, vec2 leftbottom, vec2 righttop, float smoot)
{
    return rect(leftbottom.x,righttop.x,uv.x,smoot) * rect(leftbottom.y,righttop.y,uv.y,smoot);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x = uv.x *iResolution.x/iResolution.y - (iResolution.x - iResolution.y)/(2.*iResolution.y);
    uv = uv - 0.5;

    vec2 rectPos = vec2(cos(0.66*iTime),sin(0.66*iTime))*0.5;
    float box = smoothBox(uv,vec2(-.15)+rectPos,vec2(.15)+rectPos,0.15);
    uv = uv - 0.05*box*normalize(uv);

    vec3 col = vec3(grid(uv,18.,0.01));
    //col = col - vec3(float(box > 0.9),float(box > 0.5),float(box > 0.1));

    fragColor = vec4(col,1.0);
}
