void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 cam = texture(iChannel0, uv);

    vec3 out_col;

    out_col.r = norm_sin(3.0f * PI * cam.r - PI / 2.0f);
    out_col.g = norm_sin(3.0f * PI * cam.g - PI / 2.0f);
    out_col.b = norm_sin(3.0f * PI * cam.b - PI / 2.0f);

    out_col.r /= length(cam.rgb);
    out_col.gb = cam.gb;

    fragColor = vec4(out_col, 1.0f);
    //fragColor = cam;
}

