void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;
    float Time = floor(hash11(floor(iTime * 1.5)) * 6. + 4.) + fract(iTime * 1.5);

    vec2 RayDirection = normalize(uv);
    float Max = 0.;
    float Distance = 0.;
    float MaxTry = 0.;
    float Try = 0.;
    for (float i = 0.; i < pi * 2.; i += pi / floor(Time)) {
        vec2 Angle = vec2(sin(i), cos(i));

        float Dot = dot(Angle, RayDirection);

        Try += 1.;
        if (Dot > Max) {
            Max = Dot;
            Distance = dot(uv, Angle);
            MaxTry = Try + floor(iTime * 1.5);
        }
    }

    vec3 OutterColor = hash31(MaxTry) * smoothstep(.3, .301, Distance + Max);
    vec3 InnerColor = mix(hash31(MaxTry) * .7 + .3, vec3(1., .7, .5), smoothstep(.0002, .0003, Distance - Max * Max * .04));

    Distance -= Max * Max * .2;
    vec3 col = mix(InnerColor, OutterColor, smoothstep(.1, .101, Distance));
    col = mix(col, mix(hash31(MaxTry), vec3(1., .7, .5), .7), smoothstep(.08, .081, Distance) * smoothstep(.101, .1, Distance)) * pow(Max, Time * Time);
    fragColor = vec4(col, 1.);
}

#define pi 3.14159265358

// from https://www.shadertoy.com/view/4djSRW
vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}

float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
