void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;

    float h = clamp(buf(uv),0.,1.).r+hm(uv).r;
    fragColor = vec4(h-pow(buf(uv).r,0.5),h,h,1.0);
}

float water;
float cw;

vec2 uv;

float hww(vec2 dir) {
return buf(uv+dir).r+hm(uv+dir).r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uv = fragCoord;

    if (iFrame == 0){
    water = 0.02;
    }else{
    float waterdiff;
    cw = buf(uv).r+hm(uv).r;
    waterdiff += clamp(hww(n)-cw,-buf(uv).r,buf(uv+n).r);
    waterdiff += clamp(hww(e)-cw,-buf(uv).r,buf(uv+e).r);
    waterdiff += clamp(hww(s)-cw,-buf(uv).r,buf(uv+s).r);
    waterdiff += clamp(hww(w)-cw,-buf(uv).r,buf(uv+w).r);
    waterdiff /= 4.;
    water = buf(uv).r+waterdiff;
    }
    fragColor = vec4(water);
}
