mat2 rotate2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}

vec3 hsv(float a , float b, float c){
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(a + K.xyz) * 6.0 - K.www);
    return c * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), b);
}

